// SPDX-License-Identifier:MIT

pragma solidity ^0.8.7;

import "./PriceConverter.sol";

error NotOwner();
error TakeFailTheMoney();
error BelowMinimum();

contract FundMe {
    uint256 minimumUsd = 8 * 1e18;
    using PriceConverter for uint256;
    address[] public funders;
    mapping(address => uint256) public addressToFundValue;
    address public owner;
    AggregatorV3Interface public priceFeed;

    function Fund() public payable {
        if (msg.value.getConversionRate(priceFeed) < minimumUsd) {
            revert BelowMinimum();
        }
        funders.push(msg.sender);
        addressToFundValue[msg.sender] = msg.value.getConversionRate(priceFeed);
    }

    function changeMinimumUsd(uint256 _minimumUsd) public {
        minimumUsd = _minimumUsd;
    }

    function removeMoney() public onlyOwner {
        (bool callSuccess, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        if (callSuccess != true) {
            revert TakeFailTheMoney();
        }
    }

    constructor(address priceFeedAddress) {
        owner = msg.sender;
        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert NotOwner();
        }
        _;
    }
}

